mapper.update:
UPDATE tbpost set donate_num = donate_num + 1
WHERE
post_id = #{postId,jdbcType=INTEGER}

UPDATE tbpost set praise_num = praise_num + 1
WHERE
post_id = #{postId,jdbcType=INTEGER}

UPDATE tbpost set collect_num = collect_num + 1
WHERE
post_id = #{postId,jdbcType=INTEGER}

UPDATE tbpost set pageview_num = pageview_num + 1
WHERE
post_id = #{postId,jdbcType=INTEGER}

mapper.select:
select tpt.post_id as postId, tpt.stick_top as stickTop, tpt.stick_updateTime as stickUpdateTime, tpt.project_id as projectId, tpt.project_icon as projectIcon, tpt.project_code as projectCode, tpt.project_english_name as projectEnglishName, tpt.project_chinese_name as projectChineseName, tpt.post_title as postTitle, tpt.post_type as postType, tpt.post_short_desc as postShortDesc, tpt.post_small_images as postSmallImages, tpt.comments_num as commentsNum, tpt.praise_num as praiseNum, tpt.pageview_num as pageviewNum, tpt.donate_num as donateNum, tpt.collect_num as collectNum, tpt.create_user_id as createUserId, tpt.create_user_icon as createUserIcon, tpt.create_user_signature as createUserSignature, tpt.create_user_name as createUserName, tpt.createTime as createTime, tpt.updateTime as updateTime, tpt.uuid as uuid, tpt.status as status, tpt.praise_income as praiseIncome, tpt.donate_income as donateIncome, tpt.post_total_income as postTotalIncome, tds.discuss_id as discussId, tds.disscuss_contents as disscussContents, tds.post_uuid as postUuid, tds.tag_infos as tagInfos, tds.is_nice_choice as isNiceChoice, tds.nice_choice_at as niceChoiceAt, tds.type as type, tds.dis_stick_top as disStickTop, tds.dis_stick_updateTime as disStickUpdateTime from tbpost as tpt LEFT JOIN tbdiscuss as tds on tpt.post_id=tds.post_id
ORDER BY ${sort} desc
LIMIT ${startRecord},${endRecord}

select tpt.post_id as postId, tpt.stick_top as stickTop, tpt.stick_updateTime as stickUpdateTime, tpt.project_id as projectId, tpt.project_icon as projectIcon, tpt.project_code as projectCode, tpt.project_english_name as projectEnglishName, tpt.project_chinese_name as projectChineseName, tpt.post_title as postTitle, tpt.post_type as postType, tpt.post_short_desc as postShortDesc, tpt.post_small_images as postSmallImages, tpt.comments_num as commentsNum, tpt.praise_num as praiseNum, tpt.pageview_num as pageviewNum, tpt.donate_num as donateNum, tpt.collect_num as collectNum, tpt.create_user_id as createUserId, tpt.create_user_icon as createUserIcon, tpt.create_user_signature as createUserSignature, tpt.create_user_name as createUserName, tpt.createTime as createTime, tpt.updateTime as updateTime, tpt.uuid as uuid, tpt.status as status, tpt.praise_income as praiseIncome, tpt.donate_income as donateIncome, tpt.post_total_income as postTotalIncome, tpt.type as type, tpt.dis_stick_top as disStickTop, tpt.dis_stick_updateTime as disStickUpdateTime from tbpost as tpt
ORDER BY ${sort} desc
LIMIT ${startRecord},${endRecord}

mapper.update:
UPDATE tbpost


		stick_top = #{stickTop,jdbcType=INTEGER} ,


		type = #{type,jdbcType=INTEGER} ,


		stick_updateTime = #{stickUpdateTime,jdbcType=TIMESTAMP}


WHERE
post_id = #{postId,jdbcType=INTEGER}
