mapper.update:
UPDATE tbpost set donate_num = donate_num + 1
WHERE
post_id = #{postId,jdbcType=INTEGER}

UPDATE tbpost set praise_num = praise_num + 1
WHERE
post_id = #{postId,jdbcType=INTEGER}

UPDATE tbpost set collect_num = collect_num + 1
WHERE
post_id = #{postId,jdbcType=INTEGER}

UPDATE tbpost set pageview_num = pageview_num + 1
WHERE
post_id = #{postId,jdbcType=INTEGER}

mapper.select:
select
from tbpost tp INNER JOIN tbevaluation te ON tp.post_id = te.post_id WHERE te.model_type != 1 AND tp.project_id = #{projectId}
LIMIT ${startRecord},${endRecord}

mapper.update:
update tbpost
praise_income =praise_income + #{praiseIncome},
donate_income =donate_income + #{donateIncome},
post_total_income =post_total_income + #{amount}
WHERE post_id = #{postId,jdbcType=INTEGER}
